entry.value:
package com.ding.demo.auth.service.impl;

import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;
import com.ding.demo.auth.dto.LoginRequest;
import com.ding.demo.auth.dto.LoginResponse;
import com.ding.demo.auth.dto.RegisterRequest;
import com.ding.demo.common.entity.User;
import com.ding.demo.auth.mapper.UserMapper;
import com.ding.demo.auth.service.AuthService;
import com.ding.demo.common.dto.UpdateUserRequest;
import com.ding.demo.common.dto.UserInfoResponse;
import com.ding.demo.common.utils.JwtUtils;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

@Service
public class AuthServiceImpl implements AuthService {

    @Autowired
    private UserMapper userMapper;

    @Autowired
    private PasswordEncoder passwordEncoder;

    @Autowired
    private JwtUtils jwtUtil;

    @Override
    @Transactional
    public User register(RegisterRequest request) {
        // 检查用户名是否已存在
        if (userMapper.selectCount(new QueryWrapper<User>().eq("username", request.getUsername())) > 0) {
            throw new RuntimeException("用户名已存在");
        }

        // 检查邮箱是否已存在
        if (userMapper.selectCount(new QueryWrapper<User>().eq("email", request.getEmail())) > 0) {
            throw new RuntimeException("邮箱已被注册");
        }

        // 创建新用户
        User user = new User();
        user.setUsername(request.getUsername());
        user.setPassword(passwordEncoder.encode(request.getPassword()));
        user.setEmail(request.getEmail());
        user.setPhone(request.getPhone());
        user.setUserType("USER");
        user.setEnabled(true);

        // 保存用户
        userMapper.insert(user);
        return user;
    }

    @Override
    public LoginResponse login(LoginRequest request) {
        // 查找用户
        User user = userMapper.selectOne(new QueryWrapper<User>().eq("username", request.getUsername()));
        if (user == null) {
            throw new RuntimeException("用户名或密码错误");
        }

        // 验证密码
        if (!passwordEncoder.matches(request.getPassword(), user.getPassword())) {
            throw new RuntimeException("用户名或密码错误");
        }

        // 检查用户是否启用
        if (!user.getEnabled()) {
            throw new RuntimeException("账户已被禁用");
        }

        // 生成 JWT token
        String token = jwtUtil.generateToken(user.getId().toString(), user.getUsername(), user.getUserType());
        return new LoginResponse(user.getId(), user.getUsername(), token, user.getUserType(), user.getEmail());
    }

    @Override
    public UserInfoResponse getUserInfo(String token) {
        Long userId = jwtUtil.getUserIdFromToken(token);
        User user = userMapper.selectById(userId);
        if (user == null) {
            throw new RuntimeException("用户不存在");
        }
        return new UserInfoResponse(user.getId(), user.getUsername(), user.getEmail(), user.getPhone(),
                user.getUserType());
    }

    @Override
    @Transactional
    public UserInfoResponse updateUserInfo(String token, UpdateUserRequest request) {
        Long userId = jwtUtil.getUserIdFromToken(token);
        User user = userMapper.selectById(userId);
        if (user == null) {
            throw new RuntimeException("用户不存在");
        }

        // 检查用户名是否已被其他用户使用
        if (!request.getUsername().equals(user.getUsername()) &&
                userMapper.selectCount(new QueryWrapper<User>().eq("username", request.getUsername())) > 0) {
            throw new RuntimeException("用户名已被使用");
        }

        // 检查邮箱是否已被其他用户使用
        if (!request.getEmail().equals(user.getEmail()) &&
                userMapper.selectCount(new QueryWrapper<User>().eq("email", request.getEmail())) > 0) {
            throw new RuntimeException("邮箱已被注册");
        }

        // 更新用户信息
        user.setUsername(request.getUsername());
        user.setEmail(request.getEmail());
        user.setPhone(request.getPhone());

        userMapper.updateById(user);
        return new UserInfoResponse(user.getId(), user.getUsername(), user.getEmail(), user.getPhone(),
                user.getUserType());
    }
}
